mapper.select:
select count(0) 
  from orders 
 where deleted = false and shop_id = #{shopId} 
   and order_status in 
   	  
	  	#{item}

select o.*, i.id as food_id, i.amount as amount, i.plate, f.food_name, f.image as food_image
from order_item i, 
     (select * from orders 
     	where deleted = false 
     		and shop_id = #{shopId} 
	     	 and order_status in
			  
			  	#{item}
			  
     	limit #{pageFrom}, #{pageSize}
     ) o, 
     food f
where i.deleted = false and f.deleted = false
  and o.id = i.order_id and i.item_id = f.id
  
  
  	and concat(o.address,o.contact,o.phone) like concat('%',#{keyword},'%')
  
order by o.id, i.plate, f.food_name
